def get_more_info(soup):
    """Излекаем данные об УИК по ссылке get_info_url()"""
    info_all = []
    data = soup.find("div", {"class": "center-colm"} )
    title = data.find('h2').text
    id_uik = title.split('№')[-1]
    if id_uik.isdigit():
        adr = (data.find('p').findAll('span', attrs={'id': 'address_ik'}))[0].text

        adr = [a.lstrip() for a in adr.split(',')]
        edro = str(data.find("table")).lower().count('единая')
        patr = str( data.find( "table" ) ).lower().count('патриоты')
        komu = str( data.find( "table" ) ).lower().count('коммунистическая')
        ldpr = str( data.find( "table" ) ).lower().count( 'либерально' )
        spra = str( data.find( "table" ) ).lower().count( 'справедливая' )
        zele = str( data.find( "table" ) ).lower().count( 'зеленые' )
        sobr = str( data.find( "table" ) ).lower().count( 'собрание' ) #собрание избирателей по месту работы
        shln = str( data.find( "table" ) ).lower().count( 'член' ) #статус члена

        data = (id_uik, adr, edro, patr, komu, ldpr, spra, zele, sobr, shln)
        info_all.append(data)
    return info_all


def get_soup(url):
    '''Извлекаем данные BeautifulSoup '''
    headers = {'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64; rv:71.0) Gecko/20100101 Firefox/71.0'}
    response = requests.get(url, headers=headers ).text
    soup = bs(response, 'html.parser')
    return soup


def search_select(soup):
    '''Извлекаем ссылки на уровне'''
    url_all = []
    tag = (soup.findAll( 'form', attrs={'name': 'go_reg'} ))[0]
    for i in tag.findAll('option'):
        try:
            url = (i['value'])
            url_all.append(url)
        except:
            pass
    return url_all


def get_data(soup):
    data = soup
    uf = data.find( 'a', string='Единый округ' ).parent()
    name = [t.text for t in uf]
    id_uik = (name[-1]).split('№')[1]
    name = [id_uik] + name

    data_1 = data.find('td', string='Число избирателей, внесенных в список избирателей на момент окончания голосования' ).parent()
